import React, { Component } from 'react';
import TweetList from './TweetList';
import { Form, Button, Label } from 'semantic-ui-react';

const EmployeeShow = (props) => {
	console.log(props.employeeToShow.name);
	return (
		<div>
			<Form onSubmit={props.getTweets}>
				<Label>
					Search:
					<Form.Input type="text" name="name" value={props.employeeToShow.name} />
				</Label>
				<Button type="submit">
					Search Tweets
				</Button>
			</Form>
		</div>
		)
}



