import React from 'react';
import {Form, Button, Label} from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';

const Employees = (props) => {
	console.log(props, 'props in EmployeeList');

	const employeeList = props.employees.map((employee) => {
		return (
			<tr key={employee._id}>
				<td>{employee.name} </td>
				<td>{employee.position} </td>
				<td>{employee.department} </td>
				<td>{employee.annualSalary} </td>
				<td>{employee.birthDate} </td>

				<div class="ui buttons">
				<button class="ui mini button">Edit</button>
				<div class="or" data-text="or"></div>
				<button class="ui min positive button">Delete</button>
				</div>

				<button class="ui blue primary button mini" onClick={props.deleteEmployee.bind(null, employee._id)}>Delete</button>
				<button class="ui orange button mini" onClick={props.showModal.bind(null, employee)}>Edit</button>
			</tr>
			)
	})
	return (
		<div>
			 <table class="ui unstackable inverted teal table ">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Position</th>
                        <th>Department</th>
                        <th>Salary</th>
                        <th>DOB</th>
						<th>Actions</th>
                    </tr>
                </thead>
                <tbody>
					{employeeList}
				</tbody>
            </table>   
		</div>
		)
}

export default Employees;
